var mongo=require("mongodb");
var host="localhost";
var server=new mongo.Server(host,27017,{auto_reconnect:true});//创建数据库所在的服务器服务器
var db=new mongo.Db("node",server,{safe:true});
var setTime={};
setTime.clearLoginRecord=function(ct,expires){
	setInterval(function(){
		db.open(function (err,db) {
	    	db.collection("singlelogin",function(err,collection){
				var time=new Date().getTime()-expires*60000;
				collection.remove({time:{$lte:time}},function(err){
					if(err) throw err;
					else
						db.close();
				});
			 });
		});
	},ct*60000);
}
module.exports = setTime;